member.example:
    Assume you have a form with a treeview on it. 
    
privatevoid Form1_Load(object sender, EventArgse)
{
    this.radTreeView1.NodeMouseUp += new Telerik.WinControls.UI.RadTreeView.TreeViewMouseEventHandler(radTreeView1_NodeMouseUp);
}
 
private void radTreeView1_NodeMouseUp(object sender, RadTreeViewMouseEventArgs e)
{
    this.radTreeView1.PathSeparator = ";";
    MessageBox.Show(e.Node.FullPath);
}

    Assume you have a form with a treeview, a label and a listbox on it. The example
    uses the Northwind database.
    
privatevoid Form1_Load(object sender, EventArgse)
{
    this.radTreeView1.DataSource = this.nwindDataSetBindingSource;
    this.radTreeView1.DataMember = "Customers";
    this.radTreeView1.DisplayMember = "CompanyName";
    this.radTreeView1.ValueMember = "CustomerID";
    this.radTreeView1.AllowMultiselect = true;
    this.radTreeView1.Selected += new EventHandler(radTreeView1_Selected);
}
 
private void radTreeView1_Selected(object sender, EventArgs e)
{
    this.label1.Text = this.radTreeView1.SelectedNode.Text;
    this.listBox2.Items.Clear();
    for (int i = 0; i < this.radTreeView1.SelectedNodes.Count; i++)
    {
        this.listBox2.Items.Add(this.radTreeView1.SelectedNodes[i]);
    }
}

